    public int[] twoSum(int[] numbers, int target){
        int[] result = new int[2];
        Map<Integer, Integer> map = new HashMap<Integer, Integer>();
        for(int i=0; i<numbers.length; i++){
            if(map.containsKey(target - numbers[i])){
                result[1]=i+1;
                result[0]=map.get(target - numbers[i]);
                return result;
            }
            map.put(numbers[i], i+1);
        }
        return result;
    }

    static int binary_search(int[] arr, int x){
        if(arr.length==0){
            return -1;
        }
        int low = 0, high = arr.length -1;
        while(low<=high){
            int mid = low + (high - low)/2;
            if(arr[mid]<x){
                low = mid + 1;
            }else if(arr[mid]>x){
                high = mid - 1;
            }else{
                return mid;
            }
        }
        return -1;
    }

    static int binSfirst(int[] arr, int x){
        if(arr.length ==0){
            return -1;
        }
        int low = 0, high = arr.length - 1;
        while(low < high){
            int mid = low + (high - low)/2;
            if(arr[mid]<x){
                low = mid + 1;
            }else if(arr[mid]>x){
                high = mid - 1;
            }else{
                high = mid;
            }
        }
        if(arr[high]==x){
            return high;
        }
        return -1;
    }

    static int binSlast(int[] arr, int x){
        if(arr.length == 0){
            return -1;
        }
        int low = 0, high = arr.length - 1;
        while(low+1 < high){
            int mid = low + (high - low)/2 ;
            if(arr[mid]<x){
                low = mid + 1;
            }else if(arr[mid]>x){
                high = mid - 1;
            }else{
                low = mid;
            }
        }
        if(arr[high]== x){
            return high;
        }else if(arr[low]==x){
            return low;
        }
        return -1;
    }
    public static String conver2HexStr(byte [] b)
    {
        StringBuffer result = new StringBuffer();
        for(int i = 0;i<b.length;i++)
        {
            result.append(b[i]+",");
        }
        return result.toString();
    }

    /**
     * 二进制字符串转换为byte数组,每个字节以","隔开
     * **/
    public static byte[] conver2HexToByte(String hex2Str)
    {
        String [] temp = hex2Str.split(",");
        byte [] b = new byte[temp.length];
        for(int i = 0;i<b.length;i++)
        {
            b[i] = Byte.parseByte(temp[i]);
            //b[i] = Long.valueOf(temp[i], 2).byteValue();
        }
        return b;
    }


